    /**
     * @param inputStream  æºæ–‡ä»¶è¾“å…¥æµ
     * @param outputStream pdfæ–‡ä»¶è¾“å‡ºæµ
     **/
    public static boolean imgToPdf(InputStream inputStream, OutputStream outputStream) {

        Document document = null;

        try {

            // åˆ›å»ºæ–‡æ¡£,è®¾ç½®PDFé¡µé¢çš„å¤§å° A2-A9, ä¸ªäººè§‰å¾—A3æœ€åˆé€‚
            document = new Document(PageSize.A3, 20, 20, 20, 20);

            // æ–°å»ºpdfæ–‡æ¡£,å…·ä½“é€»è¾‘çœ‹.getInstanceæ–¹æ³•
            PdfWriter.getInstance(document, outputStream);

            document.open();
            document.newPage();

            // å°†æ–‡ä»¶æµè½¬æ¢ä¸ºå­—èŠ‚æµï¼Œä¾¿äºæ ¼å¼è½¬æ¢
            BufferedInputStream bufferedInputStream = new BufferedInputStream(inputStream);
            ByteArrayOutputStream byteArrayOutputStream = new ByteArrayOutputStream();
            byte[] bytes = new byte[1024];
            int length = 0 ;
            while (-1 != (length = bufferedInputStream.read(bytes))) {
                byteArrayOutputStream.write(bytes, 0, length);
            }

            // å¤„ç†imgå›¾ç‰‡
            Image image = Image.getInstance(byteArrayOutputStream.toByteArray());

            float height = image.getHeight();
            float width = image.getWidth();

            float percent = 0.0f;
            // è®¾ç½®åƒç´ æˆ–è€…é•¿å®½é«˜,å°†ä¼šå½±å“å›¾ç‰‡çš„æ¸…æ™°åº¦ï¼Œå› ä¸ºåªæ˜¯å¯¹å›¾ç‰‡æ”¾å¤§æˆ–ç¼©å°
            if (height > width) {
                // A4 - A9
                percent = PageSize.A6.getHeight() / height * 100;
            } else {
                percent = PageSize.A6.getWidth() / width * 100;
            }

            image.setAlignment(Image.MIDDLE);
            image.scalePercent(percent);

            // å°†å›¾ç‰‡æ”¾å…¥æ–‡æ¡£ä¸­ï¼Œå®Œæˆpdfè½¬æ¢
            document.add(image);
            System.out.println("imageè½¬æ¢å®Œæ¯•");
        } catch (Exception e) {
            e.printStackTrace();
            return false;
        } finally {
            try {
                if (document != null) {
                    document.close();
                }
            } catch (Exception e) {
                e.printStackTrace();
            }
        }

        return true;
    }

