        X9ECParameters sm2ECParameters = GMNamedCurves.getByName("sm2p256v1");
        //æ„é€ domainå‚æ•°
        ECDomainParameters domainParameters = new ECDomainParameters(sm2ECParameters.getCurve(), sm2ECParameters.getG(), sm2ECParameters.getN());
